sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   g_form.hideErrorBox('phone_number');
   return validateEmail('phone_number');
}

function validateEmail(field) { 

		var no = g_form.getValue(field);

		if (no.length < 10)
		{
			g_form.showErrorBox(field, getMessage('Invalid Phone Number'));
			return false;
		}   

	for (var i = 0; i < no.length; i++) {

       if(!(no[i] >= '0' && no[i] <= '9'))
          {
            g_form.showErrorBox(field, getMessage('Invalid Phone Number'));
			return false; 
          }
 }

		return true;
	}
